import React, { useState } from 'react';
import './App.css';

function App() {
  const [step, setStep] = useState(0); // Step to manage different screens
  const [age, setAge] = useState(''); // State to hold the user's age

  const handleAgeSubmit = (e) => {
    e.preventDefault();
    // You can use the age here as needed
    console.log(`User's age: ${age}`);
    setStep(2); // Move to the next step after entering age
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Kigo</h1>
      </header>
      <main>
        <div className="box-container">
          <div className="box box1">
            <div className="inner-box">
              <div className="title">
                <p>.ñ•î ›Å Àñ‡πã ‡£≠ ‚≠ë <b>Welcome to Kigo!</b> .ñ•î ›Å Àñ‡πã ‡£≠ ‚≠ë</p>
              </div>
              {step === 0 && (
                <>
                  <div className="text-block1">
                    <p>Kigo is a haiku generator that creates haikus from your most-listened songs. All you have to do is sign in with your Spotify account!</p>
                  </div>
                  <button onClick={() => setStep(1)}>OK</button> {/* OK Button */}
                </>
              )}
              {step === 1 && (
                <>
                  <div className="text-block2">
                    <p>Please enter your age:</p>
                  </div>
                  <form onSubmit={handleAgeSubmit}>
                    <input
                      type="number"
                      value={age}
                      onChange={(e) => setAge(e.target.value)}
                      required
                    />
                    <button type="submit">Submit</button>
                  </form>
                </>
              )}
              {step === 2 && (
                <>
                  <div className="text-block3">
                    <p>Thank you for sharing your age!</p>
                    <p>Please connect your Spotify account to continue.</p>
                  </div>
                  {/* Here you could add a button or link to connect Spotify */}
                </>
              )}
            </div>
          </div>
          <div className="box box2">
            <button>Preferences</button>
            <button>Account</button>
            <button>Button3</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
